// auth.js

// Use this module to handle authentication operations, like token management
export const getToken = () => {
    // Retrieves the user token from localStorage
    return localStorage.getItem('id_token');
  };

  export const setToken = (token) => {
    // Saves user token to localStorage
    localStorage.setItem('id_token', token);
  };

  export const removeToken = () => {
    // Removes user token from localStorage
    localStorage.removeItem('id_token');
  };

  export const isLoggedIn = () => {
    // Checks if there is a saved token and if it's still valid
    const token = getToken();
    return !!token;
  };

  export const logout = () => {
    // Clears user token and profile data from localStorage
    removeToken();
    window.location.assign('/');
  };

  export default {
    getToken,
    setToken,
    removeToken,
    isLoggedIn,
    logout,
  };
